//                 <TodoItem text="아침 7시 기상하기" done={true} />
//                 <TodoItem text="오전 9시 리액트 공부하기" done={true} />
//                 <TodoItem text="오후 3시 운동하기" done={true} />
//                 <TodoItem text="오후4시 자격증 공부" done={false} />
//            </TodoListBlock>;
//   }

  function TodoList() {
    const todos = useTodoState();

    return (
      <TodoListBlock>
        {todos.map(todo => (
          <TodoItem
            key={todo.id}
            id={todo.id}
            text={todo.text}
            done={todo.done}
          />
        ))}
      </TodoListBlock>
    );
  }

export default TodoList;
